'use strict';var Utils=_interopRequireWildcard(require("../utils"));function _interopRequireWildcard(obj){if(obj&&obj.__esModule){return obj;}else{var newObj={};if(obj!=null){for(var key in obj){if(Object.prototype.hasOwnProperty.call(obj,key)){var desc=Object.defineProperty&&Object.getOwnPropertyDescriptor?Object.getOwnPropertyDescriptor(obj,key):{};if(desc.get||desc.set){Object.defineProperty(newObj,key,desc);}else{newObj[key]=obj[key];}}}}newObj.default=obj;return newObj;}}describe('viewport utils',function(){it('calculates isInViewport correctly',function(){expect(Utils.isInViewport(0,0,0,0,0)).toEqual(true);expect(Utils.isInViewport(50,100,0,20,0)).toEqual(false);expect(Utils.isInViewport(50,100,0,20,0.5)).toEqual(true);expect(Utils.isInViewport(50,100,40,20,0)).toEqual(true);expect(Utils.isInViewport(50,100,70,20,0)).toEqual(true);expect(Utils.isInViewport(50,100,90,20,0)).toEqual(true);expect(Utils.isInViewport(0,100,200,20,0.5)).toEqual(false);expect(Utils.isInViewport(0,100,200,20,1)).toEqual(true);});});